mapper.select:
select *
from t_order_info
where user_id = #{userId} and seckill_id = #{seckillId} and seckill_time = #{time}

mapper.update:
update t_order_info
set status = #{status}
where order_no = #{orderNo}
  and status = 0

update t_order_info
set status   = #{status},
    pay_date = now(),
    pay_type=#{payType}
where order_no = #{orderNo}
  and status = 0
