mapper.update:
update t_seckill_product
set stock_count = stock_count + 1
where id = #{seckillId}

mapper.select:
select stock_count
from t_seckill_product
where id = #{seckillId}

select id, product_id, seckill_price, stock_count, time, intergral, start_date
from t_seckill_product
where id = #{seckillId}
  and time = #{time}
